    // operation has completed.  
    static bool completed;  
  
    static void Main(string[] args)  
    {  
  
      // Initialize an instance of the shared recognizer.  
      using (SpeechRecognizer recognizer = new SpeechRecognizer())  
      {  
  
        // Create and load a sample grammar.  
        Grammar testGrammar =  
          new Grammar(new GrammarBuilder("testing testing"));  
        testGrammar.Name = "Test Grammar";  
        recognizer.LoadGrammar(testGrammar);  
  
        // Attach event handlers for recognition events.  
        recognizer.SpeechRecognized +=  
          new EventHandler<SpeechRecognizedEventArgs>(  
            SpeechRecognizedHandler);  
        recognizer.EmulateRecognizeCompleted +=  
          new EventHandler<EmulateRecognizeCompletedEventArgs>(  
            EmulateRecognizeCompletedHandler);  
  
        completed = false;  
  
        // Start asynchronous emulated recognition.   
        // This matches the grammar and generates a SpeechRecognized event.  
        recognizer.EmulateRecognizeAsync("testing testing");  
  
        // Wait for the asynchronous operation to complete.  
        while (!completed)  
        {  
          Thread.Sleep(333);  
        }  
  
        completed = false;  
  
        // Start asynchronous emulated recognition.  
        // This does not match the grammar or generate a SpeechRecognized event.  
        recognizer.EmulateRecognizeAsync("testing one two three");  
  
        // Wait for the asynchronous operation to complete.  
        while (!completed)  
        {  
          Thread.Sleep(333);  
        }  
      }  
  
      Console.WriteLine();  
      Console.WriteLine("Press any key to exit...");  
      Console.ReadKey();  
    }  
  
    // Handle the SpeechRecognized event.  
    static void SpeechRecognizedHandler(  
      object sender, SpeechRecognizedEventArgs e)  
    {  
      if (e.Result != null)  
      {  
        Console.WriteLine("Recognition result = {0}",  
          e.Result.Text ?? "<no text>");  
      }  
      else  
      {  
        Console.WriteLine("No recognition result");  
      }  
    }  
  
    // Handle the SpeechRecognizeCompleted event.  
    static void EmulateRecognizeCompletedHandler(  
      object sender, EmulateRecognizeCompletedEventArgs e)  
    {  
      if (e.Result == null)  
      {  
        Console.WriteLine("No result generated.");  
      }  
  
      // Indicate the asynchronous operation is complete.  
      completed = true;  
    }  
  }  
}  
  
```

  
  
## Remarks  
 <span data-ttu-id="dd84d-132">Распознаватель вызывает это событие несколько раз в секунду.</span><span class="sxs-lookup"><span data-stu-id="dd84d-132">The recognizer raises this event multiple times per second.</span></span> <span data-ttu-id="dd84d-133">Частота, с которой возникает событие, зависит от компьютера, на котором работает приложение.</span><span class="sxs-lookup"><span data-stu-id="dd84d-133">The frequency with which the event is raised depends on the computer on which the application is running.</span></span>  
  
 <span data-ttu-id="dd84d-134">Чтобы получить уровень звука во время события, используйте <xref:System.Speech.Recognition.AudioLevelUpdatedEventArgs.AudioLevel%2A> свойство связанного <xref:System.Speech.Recognition.AudioLevelUpdatedEventArgs>объекта.</span><span class="sxs-lookup"><span data-stu-id="dd84d-134">To get the audio level at the time of the event, use the <xref:System.Speech.Recognition.AudioLevelUpdatedEventArgs.AudioLevel%2A> property of the associated <xref:System.Speech.Recognition.AudioLevelUpdatedEventArgs>.</span></span> <span data-ttu-id="dd84d-135">Чтобы получить текущий уровень звука входных данных распознавателя, используйте <xref:System.Speech.Recognition.SpeechRecognizer.AudioLevel%2A> свойство распознавателя.</span><span class="sxs-lookup"><span data-stu-id="dd84d-135">To get the current audio level of the input to the recognizer, use the recognizer's <xref:System.Speech.Recognition.SpeechRecognizer.AudioLevel%2A> property.</span></span>  
  
 <span data-ttu-id="dd84d-136">При создании делегата для `AudioLevelUpdated` события определяется метод, который будет выполнять обработку события.</span><span class="sxs-lookup"><span data-stu-id="dd84d-136">When you create a delegate for an `AudioLevelUpdated` event, you identify the method that will handle the event.</span></span> <span data-ttu-id="dd84d-137">Чтобы связать событие с обработчиком событий, нужно добавить в событие экземпляр делегата.</span><span class="sxs-lookup"><span data-stu-id="dd84d-137">To associate the event with your event handler, add an instance of the delegate to the event.</span></span> <span data-ttu-id="dd84d-138">Обработчик событий вызывается всякий раз, когда происходит событие, если делегат не удален.</span><span class="sxs-lookup"><span data-stu-id="dd84d-138">The event handler is called whenever the event occurs, unless you remove the delegate.</span></span> <span data-ttu-id="dd84d-139">Дополнительные сведения о делегатах обработчиков событий см. в разделе [события и делегаты](https://go.microsoft.com/fwlink/?LinkId=162418).</span><span class="sxs-lookup"><span data-stu-id="dd84d-139">For more information about event-handler delegates, see [Events and Delegates](https://go.microsoft.com/fwlink/?LinkId=162418).</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="dd84d-140">В следующем примере обработчик `AudioLevelUpdated` события добавляется <xref:System.Speech.Recognition.SpeechRecognizer> в объект.</span><span class="sxs-lookup"><span data-stu-id="dd84d-140">The following example adds a handler for the `AudioLevelUpdated` event to a <xref:System.Speech.Recognition.SpeechRecognizer> object.</span></span> <span data-ttu-id="dd84d-141">Обработчик выводит новый звуковой уровень в консоль.</span><span class="sxs-lookup"><span data-stu-id="dd84d-141">The handler outputs the new audio level to the console.</span></span>  
  
```csharp  
private SpeechRecognizer recognizer;  
  
// Initialize the SpeechRecognizer object.   
private void Initialize()  
{  
  recognizer = new SpeechRecognizer();  
  
  // Add an event handler for the AudioLevelUpdated event.  
  recognizer.AudioLevelUpdated +=   
    new EventHandler<AudioLevelUpdatedEventArgs>(recognizer_AudioLevelUpdated);  
  
  // Add other initialization code here.  
  
}  
  
// Write the audio level to the console when the AudioLevelUpdated event is raised.  
void recognizer_AudioLevelUpdated(object sender, AudioLevelUpdatedEventArgs e)  
{  
  Console.WriteLine("The audio level is now: {0}.", e.AudioLevel);  
}  
```

  
  
## Remarks  
 <span data-ttu-id="dd84d-144">Общий распознаватель получает входные данные, пока работает распознавание речи рабочего стола.</span><span class="sxs-lookup"><span data-stu-id="dd84d-144">The shared recognizer receives input while the desktop speech recognition is running.</span></span>  
  
 <span data-ttu-id="dd84d-145">`AudioPosition` Свойство ссылается на расположение устройства ввода в созданном звуковом потоке.</span><span class="sxs-lookup"><span data-stu-id="dd84d-145">The `AudioPosition` property references the input device's position in its generated audio stream.</span></span> <span data-ttu-id="dd84d-146">Напротив, <xref:System.Speech.Recognition.SpeechRecognizer.RecognizerAudioPosition%2A> свойство ссылается на расположение распознавателя при обработке звуковых входов.</span><span class="sxs-lookup"><span data-stu-id="dd84d-146">By contrast, the <xref:System.Speech.Recognition.SpeechRecognizer.RecognizerAudioPosition%2A> property references the recognizer's position in processing audio input.</span></span> <span data-ttu-id="dd84d-147">Эти позиции могут отличаться.</span><span class="sxs-lookup"><span data-stu-id="dd84d-147">These positions can be different.</span></span>  <span data-ttu-id="dd84d-148">Например, если распознаватель получил входные данные, для которых он еще не создал результат распознавания, значение <xref:System.Speech.Recognition.SpeechRecognizer.RecognizerAudioPosition%2A> свойства меньше значения <xref:System.Speech.Recognition.SpeechRecognizer.AudioPosition%2A> свойства.</span><span class="sxs-lookup"><span data-stu-id="dd84d-148">For example, if the recognizer has received input for which it has not yet generated a recognition result then the value of the <xref:System.Speech.Recognition.SpeechRecognizer.RecognizerAudioPosition%2A> property is less than the value of the <xref:System.Speech.Recognition.SpeechRecognizer.AudioPosition%2A> property.</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="dd84d-149">В следующем примере общий распознаватель речи использует грамматику диктовки для сопоставления речевого ввода.</span><span class="sxs-lookup"><span data-stu-id="dd84d-149">In the following example, the shared speech recognizer uses a dictation grammar to match speech input.</span></span> <span data-ttu-id="dd84d-150">Обработчик <xref:System.Speech.Recognition.SpeechRecognizer.SpeechDetected> события записывает данные в <xref:System.Speech.Recognition.SpeechRecognizer.AudioPosition%2A>консоль, <xref:System.Speech.Recognition.SpeechRecognizer.RecognizerAudioPosition%2A>и <xref:System.Speech.Recognition.SpeechRecognizer.AudioLevel%2A> , когда распознаватель речи обнаруживает речь во входных данных.</span><span class="sxs-lookup"><span data-stu-id="dd84d-150">A handler for the <xref:System.Speech.Recognition.SpeechRecognizer.SpeechDetected> event writes to the console the <xref:System.Speech.Recognition.SpeechRecognizer.AudioPosition%2A>, <xref:System.Speech.Recognition.SpeechRecognizer.RecognizerAudioPosition%2A>, and  <xref:System.Speech.Recognition.SpeechRecognizer.AudioLevel%2A> when the speech recognizer detects speech at its input.</span></span>  
  
```  
using System;  
using System.Speech.Recognition;  
  
namespace SampleRecognition  
{  
  class Program  
  {  
    private static SpeechRecognizer recognizer;  
    public static void Main(string[] args)  
    {  
  
      // Initialize a shared speech recognition engine.  
      recognizer = new SpeechRecognizer();  
  
      // Add handlers for events.  
      recognizer.LoadGrammarCompleted +=   
        new EventHandler<LoadGrammarCompletedEventArgs>(recognizer_LoadGrammarCompleted);  
      recognizer.SpeechRecognized +=   
        new EventHandler<SpeechRecognizedEventArgs>(recognizer_SpeechRecognized);  
      recognizer.StateChanged +=   
        new EventHandler<StateChangedEventArgs>(recognizer_StateChanged);  
      recognizer.SpeechDetected +=   
        new EventHandler<SpeechDetectedEventArgs>(recognizer_SpeechDetected);  
  
      // Create a dictation grammar.  
      Grammar dictation = new DictationGrammar();  
      dictation.Name = "Dictation";  
  
      // Load the grammar object to the recognizer.  
      recognizer.LoadGrammarAsync(dictation);  
  
      // Keep the console window open.  
      Console.ReadLine();  
    }  
  
    // Gather information about detected speech and write it to the console.  
    static void recognizer_SpeechDetected(object sender, SpeechDetectedEventArgs e)  
    {  
      Console.WriteLine();  
      Console.WriteLine("Speech detected:");  
      Console.WriteLine("  Audio level: " + recognizer.AudioLevel);  
      Console.WriteLine("  Audio position: " + recognizer.AudioPosition);  
      Console.WriteLine("  Recognizer audio position: " + recognizer.RecognizerAudioPosition);  
    }  
  
    // Write the text of the recognition result to the console.  
    static void recognizer_SpeechRecognized(object sender, SpeechRecognizedEventArgs e)  
    {   
      Console.WriteLine("Speech recognized: " + e.Result.Text);  
  
      // Add event handler code here.  
    }  
  
    // Write the name of the loaded grammar to the console.  
    static void recognizer_LoadGrammarCompleted(object sender, LoadGrammarCompletedEventArgs e)  
    {  
      Console.WriteLine("Grammar loaded: " + e.Grammar.Name);  
    }  
  
    // Put the shared speech recognizer into "listening" mode.  
    static void recognizer_StateChanged(object sender, StateChangedEventArgs e)  
    {  
      if (e.RecognizerState != RecognizerState.Stopped)  
      {  
        recognizer.EmulateRecognizeAsync("Start listening");  
      }  
    }  
  }  
}  
  
```

  
  
